package main

import (
	"fmt"
	"sync"
	"time"
)

var i int64
var wg sync.WaitGroup
var mtx sync.Mutex
var intTMP int64

func main() {
	wg.Add(2)
	fmt.Println("hi")
	a := ""
	fmt.Println(len(a))

	chTmp := make(chan string, 9000000)

	var strTMP string
	go timetick()
	go addByteChan(a, chTmp)

	// fmt.Println(b)
	fmt.Println(len(strTMP))
	wg.Wait()
	fmt.Println(len(chTmp), "-------")
}

func timetick() {
	defer wg.Done()
	for {
		time.Sleep(1 * time.Second)
		i = i + 1
		fmt.Println(i, "~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~")
		if i > 10 {
			break
		}
	}
}

func addByteChan(a string, chTmp chan string) {
	defer wg.Done()
	for k := 0; k < 400*1024; k++ {
		chTmp <- a
		if k%400 == 0 {
			fmt.Println(k)
		}
	}
}

func addByte(a string, strTMP string) {
	defer wg.Done()
	for k := 0; k < 40*1024; k++ {
		mtx.Lock()
		strTMP = strTMP + a
		if k%400 == 0 {
			fmt.Println(k)
		}
		mtx.Unlock()
		//fmt.Println(k)
	}
}

func addint() {
	defer wg.Done()
	for k := 0; k < 500000; k++ {
		mtx.Lock()
		intTMP = intTMP + 1
		fmt.Println(intTMP)
		mtx.Unlock()
		//fmt.Println(k)
	}
}
